mapper.select:
select w.id,w.txid,w.userId,u.nickName,w.coinId,w.coinType,w.amount, w.createTime,w.auditTime,w.status from wallets_usd_withdraw w left join app_users u on w.userId=u.id where 1=1
and u.id= #{param.userId}
and status= #{param.status}
and coinId= #{param.channelType}
and w.createTime >= #{param.beginDate}
and w.createTime <= #{param.endDate}
order by status, w.createTime desc
