mapper.update:
update
    news_${news.language}
set title=#{news.title},
    description=#{news.description},
    info= #{news.info},
    source= #{news.source}
where
    id = #{id}

mapper.delete:
delete from
     news_${language}
where
    id in

        #{id}

mapper.select:
select n.*, tmp.title from news n left join news_${language} tmp on n.id = tmp.id where type=#{param.type}
and enabled= #{param.enabled}
and createTime >= #{param.beginDate}
and createTime <= #{param.endDate}
and tmp.title like CONCAT('%',#{param.title},'%')
and tmp.info like CONCAT('%',#{param.info},'%')
order by n.createTime desc
