mapper.select:
select
    sum(amount)
from
    wallet_usd_logs
where
    userId=#{userId}
    and status=0
    and type in

        #{type}


        and createTime  >=  #{beginDate}


        and createTime  <=  #{endDate}

select
    createTime, amount, action, type,status,coinId
from
    wallet_usd_logs
where
    1=1

    and userId= #{userId}


    and type= #{type}


    and createTime  >=  #{beginDate}
    and createTime  <=  #{endDate}

order by
    createTime desc
