mapper.update:
update
    wallets
set
    walletAmount=walletAmount + #{amount}
where
    userId = #{userId}

update
    wallets
set
    walletAmount=walletAmount - #{amount}
where
    userId = #{userId}

update
    wallets
set
    frozenAmount=frozenAmount - #{amount}
where
    userId = #{userId}

update
    wallets
set
    usdWalletAmount=usdWalletAmount + #{amount}
where
    userId = #{userId}

update
    wallets
set
    usdWalletAmount=usdWalletAmount - #{amount}
where
    userId = #{userId}

update
    wallets
